window.onload=function(){function addValue(n){return function(){"รท"===buttons[n].innerHTML?result.innerHTML+="/":"x"===buttons[n].innerHTML?result.innerHTML+="*":result.innerHTML+=buttons[n].innerHTML}}function btnAudio(){document.getElementById("click").play()}function btnDing(){document.getElementById("ding").play()}function calculate(i){return function(){var finalRes=result.innerHTML;result.innerHTML=eval(finalRes)}}for(var buttons=document.getElementsByTagName("button"),result=document.querySelectorAll("#display")[0],clear=document.getElementsByClassName("clear")[0],i=0;i<buttons.length;i+=1)"="===buttons[i].innerHTML?(buttons[i].addEventListener("click",calculate(i)),buttons[i].addEventListener("click",btnDing)):(buttons[i].addEventListener("click",addValue(i)),buttons[i].addEventListener("click",btnAudio));clear.onclick=function(){result.innerHTML=""}};